class GraphQL(object):
  @staticmethod
  def bounties_search() -> str:
    return "query BountiesPageSearch($input: BountySearchInput!) {\n  bountySearch(input: $input) {\n    __typename\n    ... on BountySearchConnection {\n      items {\n        ...BountyCard\n        __typename\n      }\n      pageInfo {\n        hasNextPage\n        nextCursor\n        __typename\n      }\n      __typename\n    }\n    ... on UserError {\n      message\n      __typename\n    }\n    ... on UnauthorizedError {\n      message\n      __typename\n    }\n  }\n}\n\nfragment BountyCard on Bounty {\n  id\n  title\n  descriptionPreview\n  cycles\n  deadline\n  status\n  slug\n  solverPayout\n  timeCreated\n  applicationCount\n  isUnlisted\n  solver {\n    id\n    username\n    image\n    url\n    __typename\n  }\n  user {\n    id\n    username\n    image\n    url\n    __typename\n  }\n  __typename\n}\n"

  @staticmethod
  def bounty_page() -> str:
    return "query BountyPage($input: BountyByUrlInput!) {\n  currentUser {\n    id\n    isStaff: hasRole(role: REPLIT_STAFF)\n    __typename\n  }\n  bountyByUrl(input: $input) {\n    __typename\n    ... on Bounty {\n      ...BountyPageBounty\n      __typename\n    }\n  }\n}\n\nfragment BountyPageBounty on Bounty {\n  slug\n  cycles\n  solverPayout\n  fee\n  deadline\n  description\n  descriptionPreview\n  id\n  status\n  title\n  timeCreated\n  isCurrentUserBountyPoster\n  isCurrentUserBountySolver\n  hasCurrentUserApplied\n  isUnlisted\n  contactInfo\n  contactMethod\n  applicationCount\n  user {\n    id\n    username\n    image\n    ...UserLinkUser\n    __typename\n  }\n  solver {\n    id\n    username\n    __typename\n  }\n  acceptedApplication {\n    ...BountyPageApplication\n    __typename\n  }\n  latestSubmission {\n    id\n    ...BountyPageSubmission\n    __typename\n  }\n  ...ReviewBountyHunterFormBounty\n  __typename\n}\n\nfragment UserLinkUser on User {\n  id\n  url\n  username\n  __typename\n}\n\nfragment BountyPageApplication on BountyApplication {\n  id\n  timeCreated\n  isCurrentUserApplicant\n  user {\n    id\n    username\n    image\n    ...BountyHunterReputationUserInfo\n    isInProgram: hasRole(role: TASK_PROGRAM_MEMBER)\n    ...UserLinkUser\n    socials {\n      id\n      url\n      type\n      __typename\n    }\n    __typename\n  }\n  status\n  content\n  contactInfo\n  __typename\n}\n\nfragment BountyHunterReputationUserInfo on User {\n  id\n  bountyInfo {\n    stats {\n      completedBountiesCount\n      averageHunterRating\n      numHunterReviews\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment BountyPageSubmission on BountySubmission {\n  id\n  content\n  repls {\n    id\n    nextPagePathname\n    url\n    __typename\n  }\n  user {\n    id\n    ...UserLinkUser\n    __typename\n  }\n  review {\n    id\n    ...BountyPageSubmissionReview\n    __typename\n  }\n  __typename\n}\n\nfragment BountyPageSubmissionReview on BountySubmissionReview {\n  id\n  isAccepted\n  content\n  __typename\n}\n\nfragment ReviewBountyHunterFormBounty on Bounty {\n  id\n  isCurrentUserBountyPoster\n  status\n  solver {\n    id\n    username\n    __typename\n  }\n  bountyHunterReview {\n    id\n    ...ReviewBountyHunterFormBountyHunterReview\n    __typename\n  }\n  __typename\n}\n\nfragment ReviewBountyHunterFormBountyHunterReview on BountyHunterReview {\n  id\n  timelinessRating\n  qualityRating\n  communicationRating\n  reviewText\n  timeCreated\n  __typename\n}\n"

  @staticmethod
  def create_application() -> str:
    return "mutation CreateApplication($input: CreateBountyApplicationInput!) {\n  createBountyApplication(input: $input) {\n    __typename\n    ... on BountyApplication {\n      id\n      user {\n        id\n        username\n        __typename\n      }\n      bounty {\n        id\n        ...BountyPageBounty\n        __typename\n      }\n      __typename\n    }\n    ... on NotFoundError {\n      message\n      __typename\n    }\n    ... on UnauthorizedError {\n      message\n      __typename\n    }\n    ... on UserError {\n      message\n      __typename\n    }\n  }\n}\n\nfragment BountyPageBounty on Bounty {\n  slug\n  cycles\n  solverPayout\n  fee\n  deadline\n  description\n  descriptionPreview\n  id\n  status\n  title\n  timeCreated\n  isCurrentUserBountyPoster\n  isCurrentUserBountySolver\n  hasCurrentUserApplied\n  isUnlisted\n  contactInfo\n  contactMethod\n  applicationCount\n  user {\n    id\n    username\n    image\n    ...UserLinkUser\n    __typename\n  }\n  solver {\n    id\n    username\n    __typename\n  }\n  acceptedApplication {\n    ...BountyPageApplication\n    __typename\n  }\n  latestSubmission {\n    id\n    ...BountyPageSubmission\n    __typename\n  }\n  ...ReviewBountyHunterFormBounty\n  __typename\n}\n\nfragment UserLinkUser on User {\n  id\n  url\n  username\n  __typename\n}\n\nfragment BountyPageApplication on BountyApplication {\n  id\n  timeCreated\n  isCurrentUserApplicant\n  user {\n    id\n    username\n    image\n    ...BountyHunterReputationUserInfo\n    isInProgram: hasRole(role: TASK_PROGRAM_MEMBER)\n    ...UserLinkUser\n    socials {\n      id\n      url\n      type\n      __typename\n    }\n    __typename\n  }\n  status\n  content\n  contactInfo\n  __typename\n}\n\nfragment BountyHunterReputationUserInfo on User {\n  id\n  bountyInfo {\n    stats {\n      completedBountiesCount\n      averageHunterRating\n      numHunterReviews\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment BountyPageSubmission on BountySubmission {\n  id\n  content\n  repls {\n    id\n    nextPagePathname\n    url\n    __typename\n  }\n  user {\n    id\n    ...UserLinkUser\n    __typename\n  }\n  review {\n    id\n    ...BountyPageSubmissionReview\n    __typename\n  }\n  __typename\n}\n\nfragment BountyPageSubmissionReview on BountySubmissionReview {\n  id\n  isAccepted\n  content\n  __typename\n}\n\nfragment ReviewBountyHunterFormBounty on Bounty {\n  id\n  isCurrentUserBountyPoster\n  status\n  solver {\n    id\n    username\n    __typename\n  }\n  bountyHunterReview {\n    id\n    ...ReviewBountyHunterFormBountyHunterReview\n    __typename\n  }\n  __typename\n}\n\nfragment ReviewBountyHunterFormBountyHunterReview on BountyHunterReview {\n  id\n  timelinessRating\n  qualityRating\n  communicationRating\n  reviewText\n  timeCreated\n  __typename\n}\n"